--SÄTTER VÅNINGSPLAN FÖR RUM
UPDATE Room 
SET floor = (
CASE
    WHEN Room_NR =400 THEN 4
    WHEN Room_NR >=300 THEN 3
    WHEN Room_NR >=200 AND Room_NR <300 THEN 2
    WHEN Room_NR >=100 AND Room_NR <200 THEN 1
    ELSE 0
END)
GO


--SÄTTER INVIDUELT PRIS FÖR RUM BEROENDE PÅ VÅNING OCH RUMSTYP
UPDATE Room
SET room_price = (
CASE
    WHEN Room_NR =400 THEN (SELECT price FROM Room_type WHERE room_type_id = 4)
    WHEN Room_NR >=300 AND Room_NR<400 THEN (SELECT rt.price FROM Room_type rt WHERE room.room_room_type_id = rt.room_type_id)+300
    WHEN Room_NR >=200 AND Room_NR <300 THEN (SELECT rt.price FROM Room_type rt WHERE room.room_room_type_id = rt.room_type_id)+200
    WHEN Room_NR >=100 AND Room_NR <200 THEN (SELECT rt.price FROM Room_type rt WHERE room.room_room_type_id = rt.room_type_id)+150
    ELSE (SELECT price FROM Room_type rt WHERE room.room_room_type_id = rt.room_type_id)
END)
GO


